import React from 'react'
var { Timer } = require('lib/easytimer/dist/easytimer.min.js');

// CSS
import './Home.css'

class Home extends React.Component {
  constructor(props)
  {
    super(props);

    this.state = {
      time: 1500,
      mode: 'Pomodoro'
    }
      this.convertTime = this.convertTime.bind(this)
      this.startTimer = this.startTimer.bind(this)
      this.zeroPad = this.zeroPad.bind(this)
  }

  convertTime(duration)
  {
      var min = Math.floor(duration / 60);
      var sec = duration - min * 60;
      return [min,sec];
  }

  startTimer()
  {
    var timer = new Timer();
    timer.start({countdown: true, precision: 'seconds', startValues: {seconds: Number(timeleft)}});
    timer.addEventListener('secondsUpdated', function (e) {
        min = document.getElementById("time").getElementsByClassName("minutes")[0];
        min.innerHTML = zeroPad(timer.getTimeValues().minutes, 2);
        sec = document.getElementById("ta").getElementsByClassName("seconds")[0];
        sec.innerHTML = zeroPad(timer.getTimeValues().seconds, 2);
    });
    timer.addEventListener('targetAchieved', function (e) {
        playTone()
        alarmReset()
    });
  }

  zeroPad(num, places) {
    var zero = places - num.toString().length + 1;
    return Array(+(zero > 0 && zero)).join("0") + num;
  }


  render() {
    return (
      <div>
        <div className="content-container">
          <div id="time" classname="time-container">
            <div className="minutes"></div>
            <div className="spacer"></div>
            <div className="seconds"></div>
          </div>
          <div className="controls-container">
            <button className="control-button">Pomodoro</button>
            <button className="control-button">Short Break</button>
            <button className="control-button">Long Break</button>
          </div>
          <div className="controls-container">
            <button className="control-button">Start</button>
            <button className="control-button">Stop</button>
            <button className="control-button">Resume</button>
            <button className="control-button">Reset</button>
          </div>
          <div className="options-container">
            <button className="control-button">Duration</button>
            <button className="control-button">Volume</button>
            <button className="control-button">Tone</button>
          </div>
        </div>
      </div>
  )}
}

export default (Home)
